    /*
     * Get adote by id_adote
     */
    function get_adote($id_adote)
    {
        return $this->db->get_where('adote',array('id_adote'=>$id_adote))->row_array();
    }
        
    /*
     * Get all adote
     */
    function get_all_adote()
