     */
    function update_adote($id_adote,$params)
    {
        $this->db->where('id_adote',$id_adote);
        return $this->db->update('adote',$params);
    }
    
    /*
     * function to delete adote
     */
    function delete_adote($id_adote)
    {
        return $this->db->delete('adote',array('id_adote'=>$id_adote));
